import { Router } from "express";
const router = Router();
import { validateRegisterUser, validateLogin, validateForgotPassword, validateOtpPassword, validateResetPassword, } from '../../../validates/user/user.validate.js';
import * as userController from '../controller/user.controller.js';
import { requireAuth } from '../../../middlewares/auth.middleware.js';
router.post("/register", validateRegisterUser, userController.register);
router.post("/auth/:userId", userController.auth);
router.post("/login", validateLogin, userController.login);
router.post("/password/forgot", validateForgotPassword, userController.forgotPassword);
router.post("/password/otp", validateOtpPassword, userController.otpPassword);
router.post("/password/reset", validateResetPassword, userController.resetPassword);
router.post("/detail", requireAuth, userController.detail);
export const userRoutes = router;
